import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./LoginScreen.css";
import Navbar from '../Navbar'
import Footer from '../Footer'

const LoginScreen = ({ history }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    if (localStorage.getItem("authToken")) {
      history.push("/");
    }
  }, [history]);

  const loginHandler = async (e) => {
    e.preventDefault();

    const config = {
      header: {
        "Content-Type": "application/json",
      },
    };

    try {
      const { data } = await axios.post(
        "/api/auth/login",
        { email, password },
        config
      );

      localStorage.setItem("authToken", data.token);

      history.push("/");
    } catch (error) {
      setError(error.response.data.error);
      setTimeout(() => {
        setError("");
      }, 5000);
    }
  };

  return (
  <>
  <Navbar/>
    <main class="container mt-5">
  <div class="container d-flex justify-content-center align-items-center mt-5">
    <div class="row">
        <div class="col-md-6 offset-md-3 col-xl-4 offset-xl-4">
            <div class="card shadow">
                <div class="card-body">
                    <h5 class="card-title">Login</h5>
                    {error && <span className="error-message">{error}</span>}
                    <form form onSubmit={loginHandler} class="validated-form">
                        <div class="mb-3">
                            <label class="form-label" for="email">Email</label>
                            <input class="form-control" onChange={(e) => setEmail(e.target.value)}
                            value={email} type="email" id="email" name="email" required autocomplete="off"/>
                            <div class="valid-feedback">
                                Looks good!
                            </div>
                        </div>

                        <div class="mb-3">
                            <label class="form-label" for="password">Password</label>
                            <input class="form-control" onChange={(e) => setPassword(e.target.value)}
                           value={password} type="password" id="password" name="password" required autocomplete="off" />
                            <div class="valid-feedback">
                                Looks good!
                            </div>
                        </div>
                        <button class="btn btn-success btn-block">Login</button>
                        <div class="g-signin2" data-onsuccess="onSignIn" data-longtitle="true">button</div>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>
</main>
<Footer/>
  </>
  );
};

export default LoginScreen;
